import React, { Component } from "react";

class CountDown extends Component {
  state = {
    time: 7200,
    hours: 2,
    minutes: 0,
    seconds: 0
  };

  myInterval = setInterval(() => {
    const { seconds, minutes, hours } = this.state;

    if (second > 0) {
      this.setState(seconds => ({
        seconds: seconds - 1
      }));
    } else {
      if (minutes > 0) {
        this.setState(minutes => ({
          minutes: minutes - 1,
          seconds: 59
        }));
      } else {
        if (hours > 0) {
          this.setState(hours => ({
            hours: hours - 1,
            minutes: 59
          }));
        } else {
          clearInterval(this.myInterval);
        }
      }
    }
  }, 1000);

  render() {
    return (
      <div className="time-container" style={{ margin: "5em 0 5em 0" }}>
        <div
          className="time"
          style={{ textAlign: "center", font: "100px bold" }}
        >
          {this.state.hours} : {this.state.minutes} : {this.state.seconds}
        </div>
      </div>
    );
  }
}

export default CountDown;
