import React, { Component } from "react";

import Card from "./Card";
import Payment from "./images/payment1.jpg";
import MemberCard from "./images/memberCard2.jpg";
import Menu from "./images/shabuMenu.jpg";

class App extends Component {
  render() {
    return (
      <div>
        <div className="conatiner"></div>
        <div className="container">
          <div className="row row-cols-3">
            <Card image={Menu} text="สั่งอาหาร" />
            <Card image={MemberCard} text="สำหรับสมาชิก" />
            <Card image={Payment} text="ชำระเงิน" />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
