import React, { Component } from "react";

class CountDown extends Component {
  constructor(props) {
    super(props);
    this.startTime = this.startTime.bind(this);
    this.state = {
      hours: 1,
      minutes: 30,
      seconds: 0
    };
  }

  startTime() {
    this.timer = setInterval(this.countDown, 1000);
  }

  countDown() {
    if (this.seconds > 0) {
      this.setState(({ seconds }) => ({
        seconds: seconds - 1
      }));
    } else {
      if (minutes > 0) {
        this.setState(({ minutes }) => ({
          minutes: minutes - 1,
          seconds: 59
        }));
      } else {
        if (hours > 0) {
          this.setState(({ hours }) => ({
            hours: hours - 1,
            minutes: 59
          }));
        } else {
          clearInterval(this.timer);
        }
      }
    }
  }

  render() {
    const { hours, minutes, seconds } = this.state;
    return (
      <div className="time-container" style={{ margin: "5em 0 5em 0" }}>
        <div
          className="time"
          style={{ textAlign: "center", font: "100px bold" }}
        >
          <p>
            {`0${hours}`}:{minutes < 10 ? `0${minutes}` : minutes}:
            {seconds < 10 ? `0${seconds}` : seconds}
          </p>
          <button type="submit" onClick={this.startTime}>
            Startto
          </button>
        </div>
      </div>
    );
  }
}

export default CountDown;
