import React, { Component } from "react";

class CountDown extends Component {
  state = {
    hours: 1,
    minutes: 30,
    seconds: 0
  };
  constructor(props) {
    super(props);
    this.myInterval = this.myInterval.bind(this);
  }
  myInterval = () => {
    setInterval(() => {
      const { seconds, minutes, hours } = this.state;

      if (seconds > 0) {
        this.setState(({ seconds }) => ({
          seconds: seconds - 1
        }));
      } else {
        if (minutes > 0) {
          this.setState(({ minutes }) => ({
            minutes: minutes - 1,
            seconds: 59
          }));
        } else {
          if (hours > 0) {
            this.setState(({ hours }) => ({
              hours: hours - 1,
              minutes: 59
            }));
          } else {
            alert("Time Out!");
            clearInterval(this.myInterval);
          }
        }
      }
    }, 1000);
  };

  render() {
    const { hours, minutes, seconds } = this.state;
    return (
      <div className="time-container" style={{ margin: "5em 0 5em 0" }}>
        <div
          className="time"
          style={{ textAlign: "center", font: "100px bold" }}
        >
          <p>
            {`0${hours}`}:{minutes < 10 ? `0${minutes}` : minutes}:
            {seconds < 10 ? `0${seconds}` : seconds}
          </p>
        </div>
        <button
          className="btn btn-primary"
          type="submit"
          onClick={this.myInterval}
          style={{ font: "30px", alignItems: "center" }}
        >
          Submit
        </button>
      </div>
    );
  }
}

export default CountDown;
